const { createContext } = 'react';

const MyContext = React.createContext();
const LocalStateProvider = LocalStateContext.Provider;

function CartStateProvider({ children }) {
  // This is our custom provider! We will store data (state) and functionality (updaters) in here and anyone can access it via consumers
  const cartOpen = true;
  return (
    <LocalStateProvider value={{ cartOpen }}>{children}</LocalStateProvider>
  );
}

export { CartStateProvider };
